// IsEmpty 判断字符串是否为空
func IsEmpty(s string) bool {
	return len(s) == 0
}

func IsBlank(s string) bool {
	if s == "" || strings.TrimSpace(s) == "" {
		return true
	}
	return false
}

// ToNumber 字符串转换为唯一数字
// https://stackoverflow.com/questions/5459436/how-can-i-generate-a-unique-int-from-a-unique-string
func ToNumber(s string) int {
	number := 0
	runes := []rune(s)
	for i, r := range runes {
		x := 0
		if i != 0 {
			x = int(runes[i-1])
		}
		number += ((x << 16) | (x >> 16)) ^ int(r)
	}
	return number
}

func ContainsChinese(str string) bool {
	for _, v := range str {
		if unicode.Is(unicode.Han, v) {
			return true
		}
	}
	return false
}

// ToNarrow Full width string to half width
func ToNarrow(str string) string {
	return width.Narrow.String(str)
}

// ToWiden Half with string to full width
func ToWiden(str string) string {
	return width.Widen.String(str)
}

// SplitWord split word by special seps, use empty string if seps is empty
func SplitWord(str string, seps ...string) []string {
	texts := make([]string, 0)
	if str != "" {
		n := len(seps)
		if n == 0 {
			seps = []string{" "}
			n = 1
		}
		parts := strings.Split(str, seps[0])
		if n == 1 {
			texts = parts
		} else {
			n -= 2
			for i, sep := range seps[1:] {
				m := len(parts)
				for _, part := range parts {
					for _, s := range strings.Split(part, sep) {
						s = strings.TrimSpace(s)
						if s == "" {
							continue
						}
						if n == i {
							texts = append(texts, s)
						} else {
							parts = append(parts, s)
						}
					}
				}
				parts = append(parts[:0], parts[m:]...)
			}
		}
	}
	return texts
}

func String(value interface{}) string {
	switch val := value.(type) {
	case []byte:
		return string(val)
	case string:
		return val
	}
	v := reflect.ValueOf(value)
	switch v.Kind() {
	case reflect.Invalid:
		return ""
	case reflect.Bool:
		return strconv.FormatBool(v.Bool())
	case reflect.String:
		return v.String()
	case reflect.Int, reflect.Int8, reflect.Int16, reflect.Int32, reflect.Int64:
		return strconv.FormatInt(v.Int(), 10)
	case reflect.Uint, reflect.Uint8, reflect.Uint16, reflect.Uint32, reflect.Uint64, reflect.Uintptr:
		return strconv.FormatUint(v.Uint(), 10)
	case reflect.Float64:
		return strconv.FormatFloat(v.Float(), 'f', -1, 64)
	case reflect.Float32:
		return strconv.FormatFloat(v.Float(), 'f', -1, 32)
	case reflect.Ptr, reflect.Struct, reflect.Map, reflect.Slice, reflect.Array:
		if b, err := json.Marshal(v.Interface()); err == nil {
			return string(b)
		} else {
			return ""
		}
	default:
		return fmt.Sprintf("%v", value)
	}
}

