// TrimSpecial 移除指定的内容
func TrimSpecial(s string, ss ...string) string {
	s = strings.TrimSpace(s)
	n := len(ss)
	if s == "" || n == 0 {
		return s
	}

	oldNew := make([]string, n*2)
	for i := 0; i < n; i += 2 {
		oldNew[i] = ss[i]
		oldNew[i+1] = ""
	}
	return strings.TrimSpace(strings.NewReplacer(oldNew...).Replace(s))
}

// RemoveExtraSpace 移除多余的空格
func RemoveExtraSpace(s string) string {
	if s != "" {
		s = strings.TrimSpace(s)
	}
	if s == "" {
		return s
	}
	return regexp.MustCompile("\\s{2,}").ReplaceAllLiteralString(strings.Replace(s, "　", " ", -1), " ")
}

func ToBytes(s string) []byte {
	sh := (*reflect.StringHeader)(unsafe.Pointer(&s))
	bh := reflect.SliceHeader{
		Data: sh.Data,
		Len:  sh.Len,
		Cap:  sh.Len,
	}
	return *(*[]byte)(unsafe.Pointer(&bh))
}

// WordMatched 判断 s 中是否包含 words 的单词（仅支持英文）
func WordMatched(s string, words []string, caseSensitive bool) bool {
	if s == "" || len(words) == 0 {
		return false
	}

	var b strings.Builder
	if caseSensitive {
		b.WriteString("(?i)")
	}
	b.WriteString(`\b(`)
	replacer := strings.NewReplacer(
		"\\\\", "\\\\\\",
		"(", "\\(",
		"|", "\\|",
		")", "\\)",
		"^", "\\^",
		"$", "\\$",
		".", "\\.",
		"[", "\\[",
		"*", "\\*",
		"+", "\\+",
		"?", "\\?",
		"{", "\\{",
		"-", "\\-",
		",", "\\,",
	)
	for i, word := range words {
		n := 0
		for _, w := range word {
			if !syntax.IsWordChar(w) {
				n++
			}
		}
		if n == len(word) {
			return false
		}

		if i != 0 {
			b.WriteString("|")
		}
		b.WriteString(replacer.Replace(word))
	}
	b.WriteString(`)\b`)
	if re, err := regexp.Compile(b.String()); err == nil {
		return re.MatchString(s)
	}
	return false
}

func StartsWith(s string, ss []string, caseSensitive bool) bool {
	if ss == nil || len(ss) == 0 {
		return true
	}

	has := false
	if !caseSensitive {
		s = strings.ToLower(s)
	}
	for _, prefix := range ss {
		if prefix == "" {
			has = true
		} else {
			if !caseSensitive {
				prefix = strings.ToLower(prefix)
			}
			has = strings.HasPrefix(s, prefix)
		}
		if has {
			break
		}
	}
	return has
}

func EndsWith(s string, ss []string, caseSensitive bool) bool {
	if ss == nil || len(ss) == 0 {
		return true
	}

	has := false
	if !caseSensitive {
		s = strings.ToLower(s)
	}
	for _, suffix := range ss {
		if suffix == "" {
			has = true
		} else {
			if !caseSensitive {
				suffix = strings.ToLower(suffix)
			}
			has = strings.HasSuffix(s, suffix)
		}
		if has {
			break
		}
	}
	return has
}

func Contains(s string, ss []string, caseSensitive bool) bool {
	in := false
	if !caseSensitive {
		s = strings.ToLower(s)
	}
	for _, substr := range ss {
		if substr == "" {
			in = true
		} else {
			if !caseSensitive {
				substr = strings.ToLower(substr)
			}
			in = strings.Contains(s, substr)
		}
		if in {
			break
		}
	}
	return in
}
